// src/components/QuizDetail.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import QuizNavbar from '../components/QuizNavbar';
import axios from 'axios';
import '../css/QuizDetail.css';

function QuizDetail() {
  const { id } = useParams(); // URL에서 id를 추출
  const [quiz, setQuiz] = useState(null); // 퀴즈 상세 정보를 저장할 상태
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState(null); // 오류 상태
  const [submittedAnswer, setSubmittedAnswer] = useState(''); // 사용자가 제출한 답변
  const [answerMessage, setAnswerMessage] = useState(''); // 정답 처리 메시지

  useEffect(() => {
    // 데이터 가져오기
    const fetchQuiz = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/quizzes/get/${id}`);
        setQuiz(response.data.data);
      } catch (error) {
        setError(error.message); // 오류 상태 업데이트
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchQuiz(); // 데이터 가져오기 호출
  }, [id]); // id가 변경될 때마다 실행

  const handleAnswerSubmit = async () => {
    try {
      const response = await axios.post(`http://localhost:8080/api/quizzes/checkAnswer`, {
        quizId: id,
        answer: submittedAnswer
      });

      if (response.data.correct) {
        setAnswerMessage('정답입니다!');
      } else {
        setAnswerMessage('틀린 답변입니다.');
      }
    } catch (error) {
      setError(error.message); // 오류 상태 업데이트
    }
  };

  if (loading) return <p>로딩 중...</p>;
  if (error) return <p>오류 발생: {error}</p>;
  if (!quiz) return <p>퀴즈 정보를 찾을 수 없습니다.</p>;

  return (
    <div className="quiz-detail-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='quizNavbar-container'>
        <QuizNavbar />
      </div>
      <div></div>
      <h1>{quiz.title}</h1>
      <p>{quiz.category}</p>
      <p>{quiz.content}</p>

      <div className="answer-container">
        <input
          type="text"
          placeholder="정답을 입력하세요"
          value={submittedAnswer}
          onChange={(e) => setSubmittedAnswer(e.target.value)}
        />
        <button
          className="answer-button"
          onClick={handleAnswerSubmit}
        >
          정답 제출
        </button>
      </div>

      {answerMessage && <p className="answer-message">{answerMessage}</p>}
    </div>
  );
}

export default QuizDetail;
