import React, { useEffect, useState } from 'react';
import axiosInstance from '../../contexts/axiosInstance'; // Ensure you have your axiosInstance configured
import '../../css/MyResume.css'; // CSS file for styling

function MyResume() {
    const [resumes, setResumes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchResumes = async () => {
            try {
                const userId = 1; // Replace with actual user ID retrieval logic
                const userRole = 'USER'; // Replace with actual role retrieval logic

                const response = await axiosInstance.get('/api/v1/resumes/my', {
                    headers: {
                        'X-UserId': userId,
                        'X-Role': userRole,
                    },
                });

                setResumes(response.data.data); // Adjust according to your API response structure
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        fetchResumes();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error fetching resumes: {error.message}</div>;

    return (
        <div className="my-resume-page">
            <h2>내 이력서</h2>
            {resumes.length > 0 ? (
                <ul>
                    {resumes.map((resume, index) => (
                        <li key={index} className="resume-item">
                            <h3>{resume.title}</h3>
                            <p>{resume.createdAt}</p>
                            <p>{resume.description}</p> {/* Adjust fields as necessary */}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>등록된 이력서가 없습니다.</p>
            )}
        </div>
    );
}

export default MyResume;
