import React, { useContext } from 'react';
import '../css/UserNavbar.css';
import { AuthContext } from '../contexts/AuthContext';

function UserNavbar({ isOpen, toggleSidebar }) {
    const { user } = useContext(AuthContext);


  return (
    <div className={`user-sidebar ${isOpen ? 'open' : ''}`}>
        <a className='user-sidebar-logo'>개발 세발</a>
      <button className="user-sidebar-close-button" onClick={toggleSidebar}>X</button>
        <div className='user-slider-profile'>
            <span className="user-slider-email">{user.email}</span>
        </div>
      <ul className="user-slidebar-list">
        <li><a>계정</a></li>
        <li><a>이력서</a></li>
        <li><a>포트폴리오</a></li>
      </ul>
    </div>
  );
}

export default UserNavbar;
