import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import QuizCreate from './QuizCreate';
import QuizEdit from './QuizEdit';
import QuizDelete from './QuizDelete';

function Quiz() {
  const [selectedMenu, setSelectedMenu] = useState('create');

  const renderContent = () => {
    switch (selectedMenu) {
      case 'create':
        return <QuizCreate />;
      case 'edit':
        return <QuizEdit />;
      case 'delete':
        return <QuizDelete />;
      default:
        return <QuizCreate />;
    }
  };

  return (
    <div className='container'>
      <div>
        <Navbar />
      </div>
      <div>
        <h1>퀴즈 페이지</h1>
        <div className='menu'>
          <button onClick={() => setSelectedMenu('create')}>퀴즈 만들기</button>
          <button onClick={() => setSelectedMenu('edit')}>퀴즈 수정하기</button>
          <button onClick={() => setSelectedMenu('delete')}>퀴즈 삭제하기</button>
        </div>
        <div className='content'>
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default Quiz;
