import React, { useState, useContext, useEffect } from 'react';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import '../../css/Profile.css';
import { AuthContext } from '../../contexts/AuthContext';
import UserMenubar from '../../components/UserMenubar';

function Profile() {
    const { user } = useContext(AuthContext); 
    const [nickName, setNickName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');

    // 컴포넌트가 마운트될 때 user 정보로 상태 초기화
    useEffect(() => {
        if (user) {
            setNickName(user.nickName || '');
            setPhoneNumber(user.phoneNumber || '');
        }
    }, [user]);


    const handleSave = () => {

        alert('프로필이 저장되었습니다.');
    };

    return (
        <div className="user-profile-page">
            <div className="header-container">
                <Header />
            </div>
            <div className="navbar-container">
                <Navbar />
            </div>
            <div></div>
            <div className="user-profile-container">
                <h3 className="profile-title">프로필</h3>
                <div className="user-profile-details">
                    <img
                        src={user?.profilePicture || 'default-profile.png'}
                        alt="Profile"
                        className="profile-picture"
                    />
                    <div className="profile-info-list">
                        <div className="profile-info-item">
                            <label>닉네임</label>
                            <input
                                type="text"
                                value={nickName} // 상태에서 관리하는 nickName 사용
                                onChange={(e) => setNickName(e.target.value)}
                                className="profile-input"
                            />
                        </div>
                        <div className="profile-info-item">
                            <label>이메일</label>
                            <input
                                type="email"
                                value={user?.email || ''} // user의 이메일은 읽기 전용
                                readOnly // 이메일 필드를 읽기 전용으로 설정
                                className="profile-input"
                            />
                        </div>
                        <div className="profile-info-item">
                            <label>전화번호</label>
                            <input
                                type="text"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                className="profile-input"
                            />
                        </div>
                        <button onClick={handleSave} className="profile-save-button">저장</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Profile;
