import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // 추가
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import QuizNavbar from '../components/QuizNavbar';
import '../css/Quiz.css';
import axios from 'axios';

function Quiz() {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const quizzesPerPage = 12;

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/quizzes/getAll', {
          params: {
            page: currentPage - 1,
            size: quizzesPerPage
          }
        });

        if (response.data && response.data.data) {
          setQuizzes(Array.isArray(response.data.data.content) ? response.data.data.content : []);
          setTotalPages(response.data.data.totalPages || 1);
        } else {
          setQuizzes([]);
          setTotalPages(1);
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchQuizzes();
  }, [currentPage]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="quiz-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='quizNavbar-container'>
        <QuizNavbar />
      </div>
      <div className="quiz-content">
        {loading && <p className="loading-message">로딩 중...</p>}
        {error && <p className="error-message">오류 발생: {error}</p>}
        {!loading && !error && quizzes.length > 0 && (
          <div className="quiz-list">
            {quizzes.map(quiz => (
              <div className="quiz-card" key={quiz.id}>
                <h3>
                  <Link to={`/quizzes/${quiz.id}`}>{quiz.title}</Link> {/* 링크 추가 */}
                </h3>
                <p>{quiz.content}</p>
              </div>
            ))}
          </div>
        )}
        {!loading && !error && quizzes.length === 0 && (
          <p className="no-quizzes-message">퀴즈가 없습니다.</p>
        )}
        <div className="pagination">
          <button
            className="page-button"
            disabled={currentPage === 1}
            onClick={() => handlePageChange(1)}
          >
            처음
          </button>
          <button
            className="page-button"
            disabled={currentPage === 1}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            이전
          </button>
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              className={`page-button ${currentPage === i + 1 ? 'active' : ''}`}
              onClick={() => handlePageChange(i + 1)}
            >
              {i + 1}
            </button>
          ))}
          <button
            className="page-button"
            disabled={currentPage === totalPages}
            onClick={() => handlePageChange(currentPage + 1)}
          >
            다음
          </button>
          <button
            className="page-button"
            disabled={currentPage === totalPages}
            onClick={() => handlePageChange(totalPages)}
          >
            마지막
          </button>
        </div>
      </div>
    </div>
  );
}

export default Quiz;
