import React, { useState, useEffect } from 'react';
import axiosInstance from '../../contexts/axiosInstance';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css'
import { Link } from 'react-router-dom'; // Link 추가


function TeamSidejob() {

  const [teams, setTeams] = useState([]); // 퀴즈 데이터를 저장할 상태
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 상태
  const [totalPages, setTotalPages] = useState(1); // 총 페이지 수 상태
  const teamsPerPage = 12; // 페이지 당 퀴즈 수
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState(null); // 오류 상태

  useEffect(() => {
    // 데이터 가져오기
    const fetchTeams = async () => {
      try {

        const response = await axiosInstance.get('/api/v1/teams/search', {
          params: {
            teamCategory: "SIDE_JOB",
            page: currentPage, 
            size: teamsPerPage
          }
        });

        // 서버 응답 로그 출력
        console.log(response.data.data);

        // 데이터 검증 및 상태 업데이트
        if (response.data && response.data.data) {
          setTeams(Array.isArray(response.data.data.content) ? response.data.data.content : []);
          setTotalPages(response.data.data.page.totalPages || 1);
        } else {
          setTeams([]);
          setTotalPages(1);
        }
      } catch (error) {
        setError(error.message); // 오류 상태 업데이트
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchTeams(); // 데이터 가져오기 호출
  }, [currentPage]); // currentPage가 변경될 때마다 실행


  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar />
      </div>

      <div className="team-content">
        <div className='team-container'>
        <h2>사이드잡</h2>        
        <div className="team-list">
          {currentItems.map((item, index) => (
            <div key={index} className="team-card">
              <h3 className="team-card-title">{item.title}</h3>
              <p className="team-card-description">{item.description}</p>
            </div>
          ))}
        </div>
        <div className="pagination">
        <button
            className="page-button"
          >
            처음
          </button>
          <button
            className="page-button"
          >
            이전
          </button>
          {Array.from({ length: totalPages }, (_, index) => (
            <div
              key={index}
              className={`page-tile ${currentPage === index + 1 ? 'active' : ''}`}
              onClick={() => handlePageClick(index + 1)}
            >
              {index + 1}
            </div>
          ))}
                  <button
            className="page-button"
          >
            다음
          </button>
          <button
            className="page-button"
          >
            마지막
          </button>
        </div>
        </div>
      </div>
    </div>
  );
}

export default TeamSidejob;
