import React, { useState } from 'react';
import axiosInstance from '../contexts/axiosInstance';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css'




function Team() {

  const [teams, setTeams] = useState([]); // 퀴즈 데이터를 저장할 상태
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 12;


  // Get current items based on the current page
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = mockData.slice(indexOfFirstItem, indexOfLastItem);

  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  useEffect(() => {
    // 데이터 가져오기
    const fetchTeams = async () => {
      try {

        const response = await axiosInstance.get('/api/teams', {
          params: {
            page: currentPage - 1, // Spring Data JPA는 0부터 시작합니다.
            size: quizzesPerPage
          }
        });

        // 서버 응답 로그 출력
        console.log(response.data.data);

        // 데이터 검증 및 상태 업데이트
        if (response.data && response.data.data) {
          setQuizzes(Array.isArray(response.data.data.content) ? response.data.data.content : []);
          setTotalPages(response.data.data.page.totalPages || 1);
        } else {
          setQuizzes([]);
          setTotalPages(1);
        }
      } catch (error) {
        setError(error.message); // 오류 상태 업데이트
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchQuizzes(); // 데이터 가져오기 호출
  }, [currentPage]); // currentPage가 변경될 때마다 실행

  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar />
      </div>

      <div className="team-content">
        <div className='team-container'>
          <h2>전체 팀</h2>        
        <div className="team-list">
          {currentItems.map((item, index) => (
            <div key={index} className="team-card">
              <h3 className="team-card-title">{item.title}</h3>
              <p className="team-card-description">{item.description}</p>
            </div>
          ))}
        </div>
        <div className="pagination">
        <button
            className="page-button"
          >
            처음
          </button>
          <button
            className="page-button"
          >
            이전
          </button>
          {Array.from({ length: totalPages }, (_, index) => (
            <div
              key={index}
              className={`page-tile ${currentPage === index + 1 ? 'active' : ''}`}
              onClick={() => handlePageClick(index + 1)}
            >
              {index + 1}
            </div>
          ))}
                  <button
            className="page-button"
          >
            다음
          </button>
          <button
            className="page-button"
          >
            마지막
          </button>
        </div>

      </div>
    </div>
    </div>
  );
}

export default Team;
