import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import QuizNavbar from '../components/QuizNavbar';
import '../css/QuizAi.css';
import axios from 'axios';
import { GoogleGenerativeAI } from "@google/generative-ai";



function QuizAi() {
  const [inputText, setInputText] = useState('');
  const [responseText, setResponseText] = useState('');
  const [loading, setLoading] = useState(false);
  const genAI = new GoogleGenerativeAI('AIzaSyBInJ-RGa49RYukD-vnTY-hK8MpnS6E3_0');

  const handleSubmit = async () => {

    setLoading(true);

    try {
        const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash"});

        const result = await model.generateContent(inputText);
        const response = result.response;
        const text = response.text();
        console.log(text);

        setResponseText(text);
    } catch (error) {
      console.error('Error sending request:', error);
      setResponseText('오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }





  };

  return (
    <div>
        <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='quizNavbar-container'>
        <QuizNavbar />
      </div>


      <div className='quizAi-content'>
      <h2>AI 질문하기</h2>

        <h3>질문 입력:</h3>
            <input
              type="text"
              id="inputText"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              required
              className="question-input"
            />
                  <button onClick={handleSubmit} className="quizAi-button">전송</button>
          </div>

      {loading && <p>요청 처리 중...</p>}

      {responseText && (
        <div>
          <h2>응답 결과:</h2>
          <pre>{responseText}</pre>
        </div>
      )}
    </div>
  );
};

export default QuizAi;
