import React, { useState } from 'react';
import axios from 'axios';

const TextRequestComponent = () => {
  const [inputText, setInputText] = useState('');
  const [responseText, setResponseText] = useState('');
  const [loading, setLoading] = useState(false);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = async () => {
    if (!inputText) {
      alert('텍스트를 입력하세요.');
      return;
    }

    const requestPayload = {
      contents: [
        {
          parts: [
            {
              text: inputText,
            },
          ],
        },
      ],
    };

    setLoading(true);

    try {
      const response = await axios.post('YOUR_API_ENDPOINT', requestPayload, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setResponseText(response.data);
    } catch (error) {
      console.error('Error sending request:', error);
      setResponseText('오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Google AI Text Request Example</h1>
      <textarea
        rows="4"
        cols="50"
        placeholder="텍스트를 입력하세요."
        value={inputText}
        onChange={handleInputChange}
      />
      <br />
      <button onClick={handleSubmit}>전송</button>

      {loading && <p>요청 처리 중...</p>}

      {responseText && (
        <div>
          <h2>응답 결과:</h2>
          <pre>{JSON.stringify(responseText, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default QuizAi;
