import React, { useState, useContext } from 'react';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import '../../css/UserDelete.css';
import { AuthContext } from '../../contexts/AuthContext';
import UserMenubar from '../../components/UserMenubar';
import axiosInstance from '../../contexts/axiosInstance';

function UserDelete() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  // 이메일 입력 핸들러
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  // 사용자 삭제 요청 핸들러
  const handleDelete = async () => {
    try {
      const response = await axiosInstance.delete('/api/users', {
        data: { email },
      });
      if (response.status === 200) {
        setMessage('사용자가 성공적으로 삭제되었습니다.');
      } else {
        setMessage('삭제 중 문제가 발생했습니다.');
      }
    } catch (error) {
      setMessage('삭제 요청 중 오류가 발생했습니다.');
      console.error(error);
    }
  };

  return (
    <div className="user-delete-page">
      <div className="header-container">
        <Header />
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className="user-menubar-container">
        <UserMenubar />
      </div>
      <div className="delete-form-container">
        <h2>사용자 삭제</h2>
        <input
          type="email"
          placeholder="삭제할 이메일을 입력하세요"
          value={email}
          onChange={handleEmailChange}
          className="email-input"
        />
        <button onClick={handleDelete} className="delete-button">
          삭제하기
        </button>
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
}

export default UserDelete;
