import React, { useContext, useState } from 'react';
import '../css/UserSidebar.css';
import { AuthContext } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

function UserMenubar() {
  const { user } = useContext(AuthContext);
  const [isAccountOpen, setIsAccountOpen] = useState(false);
  const [isResumeOpen, setIsResumeOpen] = useState(false);
  const [isPortfolioOpen, setIsPortfolioOpen] = useState(false);


  return (
    <div className="user-sidebar">
      <a className='user-sidebar-logo'>개발 세발</a>

      <ul className="user-sidebar-list">
        <li>
          <a className="user-sidebar-mainmenu" onClick={toggleAccountMenu}>
            계정
          </a>
          {isAccountOpen && (
            <ul className="user-sidebar-submenu">
              <li><Link to="/user/account/profile">프로필</Link></li>
            </ul>
          )}
        </li>
        <li>
          <a className="user-sidebar-mainmenu" onClick={toggleResumeMenu}>
            이력서
          </a>
          {isResumeOpen && (
            <ul className="user-sidebar-submenu">
              <li><Link to="/user/resume/create">이력서 작성</Link></li>
              <li><Link to="/user/resume/view">이력서 조회</Link></li>
            </ul>
          )}
        </li>
        <li>
          <a className="user-sidebar-mainmenu">
            포트폴리오
          </a>
          {isPortfolioOpen && (
            <ul className="user-sidebar-submenu">
              <li><Link to="/user/portfolio/create">포트폴리오 작성</Link></li>
              <li><Link to="/user/portfolio/view">포트폴리오 조회</Link></li>
            </ul>
          )}
        </li>
      </ul>
    </div>
  );
}

export default UserMenubar;
