import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import QuizNavbar from '../components/QuizNavbar';
import '../css/QuizCreate.css';
import { Link } from 'react-router-dom';

function QuizCreate() {

    const [category, setCategory] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [answer, setAnswer] = useState('');
    const categories = ['기초', '중급', '고급', '문제풀이', '기타'];


    const handleSubmit = (e) => {
      e.preventDefault();
      // 폼 제출 시 처리할 로직을 여기에 추가합니다.
      console.log('문제 분류:', category);
      console.log('문제 제목:', title);
      console.log('문제 내용:', content);
      console.log('문제 정답값:', answer);

      // API 호출 또는 상태 업데이트 등 처리
    };

  return (
    <div className="quiz-create-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='quizNavbar-container'>
        <QuizNavbar />
      </div>
      <div className="quiz-create-content">
        <h2>퀴즈 만들기</h2>
        <form onSubmit={handleSubmit} className="quiz-create-form">
          <div className="quiz-create-form-group">
                    <label htmlFor="category">퀴즈 분류:</label>
                        <select
                            id="category"
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                            required
                        >
                            <option value="">선택하세요</option>
                            {categories.map((cat) => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>
          </div>
          <div className="quiz-create-form-group">
            <label htmlFor="title">퀴즈 제목:</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="quiz-create-form-group">
            <label htmlFor="content">퀴즈 내용:</label>
            <textarea
              id="content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            />
          </div>
          <div className="quiz-create-form-group">
            <label htmlFor="answer">퀴즈 정답:</label>
            <input
              type="text"
              id="answer"
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="quiz-create-submit-button">퀴즈 제출</button>
        </form>
      </div>
    </div>
  );
}

export default QuizCreate;
