import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/Header.css';
import { AuthContext } from '../contexts/AuthContext';
import UserNavbar from './UserNavbar';

function Header() {
  const { logout, user } = useContext(AuthContext);
  const [showSidebar, setShowSidebar] = useState(false);

  const toggleSidebar = () => {
    setShowSidebar(prevState => !prevState);  // Toggle the sidebar
  };

  return (
    <>
      <header className="header">
        <div className="logo-container">
          <Link to="/" className="logo">
            <span className="logo-text">개발 세발</span>
          </Link>
        </div>
        <div className="auth-links">
          {user ? (
            <>
              <span onClick={toggleSidebar} className="user-email">{user.email}</span>
              <button onClick={logout} className="logout-button">로그아웃</button>
            </>
          ) : (
            <>
              <Link to="/login" className="auth-link">로그인</Link>
              <Link to="/signup" className="auth-link">회원가입</Link>
            </>
          )}
        </div>
      </header>
      <UserNavbar isActive={showSidebar} toggleSidebar={toggleSidebar} /> {/* Pass `isActive` */}
    </>
  );
}

export default Header;
