import React, { useState, useEffect } from 'react';
import axiosInstance from '../../contexts/axiosInstance';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css'




function Team() {

  const [teams, setTeams] = useState([]); // 퀴즈 데이터를 저장할 상태



  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 상태
  const [totalPages, setTotalPages] = useState(1); // 총 페이지 수 상태
  const teamsPerPage = 12; // 페이지 당 퀴즈 수

  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState(null); // 오류 상태

  useEffect(() => {
    // 데이터 가져오기
    const fetchTeams = async () => {
      try {

        const response = await axiosInstance.get('/api/v1/teams', {
          params: {
            page: currentPage, // Spring Data JPA는 0부터 시작합니다.
            size: teamsPerPage
          }
        });

        // 서버 응답 로그 출력
        console.log(response.data.data);

        // 데이터 검증 및 상태 업데이트
        if (response.data && response.data.data) {
          setTeams(Array.isArray(response.data.data.content) ? response.data.data.content : []);
          setTotalPages(response.data.data.page.totalPages || 1);
        } else {
          setTeams([]);
          setTotalPages(1);
        }
      } catch (error) {
        setError(error.message); // 오류 상태 업데이트
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchTeams(); // 데이터 가져오기 호출
  }, [currentPage]); // currentPage가 변경될 때마다 실행

  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar />
      </div>

      <div className="team-content">
        <div className='team-container'>
          <h2>전체 팀</h2>        
        <div className="team-list">
          {teams.map((team, index) => (
            <div key={index} className="team-card">
              <p className="team-card-teamCategory">{team.teamCategory}</p>
              <h3 className="team-card-title">{team.title}</h3>
              <p className="team-card-description">{team.description}</p>
              <p className="team-card-memberNumber">{team.memberNumber}</p>

            </div>
          ))}
        </div>

        {!loading && !error && quizzes.length > 0 && (
          <div className="quiz-list">
            {teams.map(team => (
              <div className="team-card" key={team.id}>
                <Link to={`/api/v1/team/get/${team.id}`} className="team-link">
                  <span>{quiz.category}</span>
                  <h3>{quiz.title}</h3>
                  <p>{quiz.content}</p>
                </Link>
              </div>
            ))}
          </div>
        )}
        {!loading && !error && quizzes.length === 0 && (
          <p className="no-quizzes-message">퀴즈가 없습니다.</p>
        )}

        <div className="pagination">


        </div>

      </div>
    </div>
    </div>
  );
}

export default Team;
