import React, { useState, useEffect } from 'react';
import axiosInstance from '../../contexts/axiosInstance';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css';
import { Link } from 'react-router-dom'; // Link 추가

function TeamShortProject() {
  const [teams, setTeams] = useState([]); 
  const [currentPage, setCurrentPage] = useState(1); 
  const [totalPages, setTotalPages] = useState(1); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null); 
  const [searchTerm, setSearchTerm] = useState(""); // 검색어 상태 추가
  const [sortOption, setSortOption] = useState("최신순"); // 정렬 상태 추가
  const teamsPerPage = 12; 

  const fetchTeams = async () => {
    try {
      const response = await axiosInstance.get('/api/v1/teams/search', {
        params: {
          page: currentPage, 
          size: teamsPerPage,
          searchTerm: searchTerm, // 검색어를 요청 파라미터로 추가
          sortOption: sortOption // 정렬 옵션을 요청 파라미터로 추가
        }
      });

      console.log(response.data.data);

      if (response.data && response.data.data) {
        setTeams(Array.isArray(response.data.data.content) ? response.data.data.content : []);
        setTotalPages(response.data.data.page.totalPages || 1);
      } else {
        setTeams([]);
        setTotalPages(1);
      }
    } catch (error) {
      setError(error.message); 
    } finally {
      setLoading(false); 
    }
  };


  // 검색 버튼 클릭 시 호출되는 함수
  const handleSearch = () => {
    setCurrentPage(1); // 검색 시 첫 페이지로 초기화
    fetchTeams(); // 팀 검색 실행
  };

  useEffect(() => {
    fetchTeams(); // 초기 데이터 로딩
  }, [currentPage]); // currentPage와 searchTerm이 변경될 때마다 실행


  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar 
          searchTerm={searchTerm} 
          setSearchTerm={setSearchTerm} 
          sortOption={sortOption}
          setSortOption={setSortOption}
          onSearch={handleSearch} // 검색 처리 함수 전달
        />
      </div>

      <div className="team-content">
        <div className='team-container'>
          <h2>전체 팀</h2>        
          <p>{teams.length} 개의 팀이 검색되었습니다.</p>
          <div className="team-list">

            {loading && <p className='loading-message'>로딩 중...</p>}

            {/* {error && <p className="error-message">오류가 발생했습니다: {error}</p>} */}

            {teams.length === 0 && (
                <p className="no-teams-message">팀이 없습니다.</p>
            )}

            {teams.map(team => (
              <div className="team-card" key={team.id}>
                <Link to={`/team/${team.id}`} className="team-link">
                  <span>{team.teamCategory}</span>
                  <h3>{team.title}</h3>
                  <p>{team.description}</p>
                  <p>팀원 수: {team.memberNumber}명</p>
                </Link>

              </div>
            ))}
          </div>
        </div>

        <div className="pagination">
          {/* Pagination logic can be added here */}
        </div>
      </div>
    </div>
  );
}

export default TeamShortProject;
