import React, { useContext } from 'react';
import '../css/UserNavbar.css';
import { AuthContext } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';


function UserNavbar({ isOpen, toggleSidebar }) {
    const { user } = useContext(AuthContext);


  return (
    <div className={`user-sidebar ${isOpen ? 'open' : ''}`}>
        <a className='user-sidebar-logo'>개발 세발</a>
      <button className="user-sidebar-close-button" onClick={toggleSidebar}>X</button>
        <div className='user-sidebar-profile'>
        {user ? (
          <>
            <span className="user-email" onClick={toggleSidebar}>{user.email}</span>
          </>
        ) : (
          <>
            <Link to="/login" className="auth-link">로그인</Link>
            <Link to="/signup" className="auth-link">회원가입</Link>
          </>
        )}
        </div>

      <ul className="user-slidebar-list">
        <li><a>계정</a></li>
        <li><a>이력서</a></li>
        <li><a>포트폴리오</a></li>
      </ul>
    </div>
  );
}

export default UserNavbar;
