import React, { useState, useContext } from 'react';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import '../../css/Profile.css';
import { AuthContext } from '../../contexts/AuthContext';

function ProfileEdit() {
    const { user, updateUser } = useContext(AuthContext); // updateUser 함수도 가져온다고 가정
    const [nickName, setNickName] = useState(user?.nickName || '');
    const [email, setEmail] = useState(user?.email || '');
    const [phoneNumber, setPhoneNumber] = useState(user?.phoneNumber || '');

    const handleSave = () => {
        // 여기에서 API 호출을 통해 서버에 사용자 정보를 업데이트하는 로직 추가
        const updatedUser = { ...user, nickName, email, phoneNumber };
        updateUser(updatedUser); // 업데이트 함수 호출
        alert('프로필이 저장되었습니다.');
    };

    return (
        <div className="user-profile-page">
            <div className="header-container">
                <Header />
            </div>
            <div className="navbar-container">
                <Navbar />
            </div>
            <div className="user-profile-container">
                <h3 className="profile-title">프로필 수정</h3>
                <div className="user-profile-details">
                    <img
                        src={user?.profilePicture || 'default-profile.png'}
                        alt="Profile"
                        className="profile-picture"
                    />
                    <div className="profile-info-list">
                        <div className="profile-info-item">
                            <label>닉네임</label>
                            <input
                                type="text"
                                value={nickName}
                                onChange={(e) => setNickName(e.target.value)}
                                className="profile-input"
                            />
                        </div>
                        <div className="profile-info-item">
                            <label>이메일</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="profile-input"
                            />
                        </div>
                        <div className="profile-info-item">
                            <label>전화번호</label>
                            <input
                                type="text"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                className="profile-input"
                            />
                        </div>
                        <button onClick={handleSave} className="profile-save-button">Save</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProfileEdit;
