  { title: "팀 프로젝트 10", description: "설명 12" },
  { title: "팀 프로젝트 10", description: "설명 13" },

  // ... (더 많은 항목 추가 가능)
];

function TeamDetail() {
    const { id } = useParams(); // URL에서 id를 추출

    const [team, setTeam] = useState(); // 퀴즈 데이터를 저장할 상태

    useEffect(() => {


        // 데이터 가져오기
        const fetchTeams = async () => {
          try {

            const response = await axiosInstance.get('/api/v1/teams', {
              params: {
                page: currentPage, // Spring Data JPA는 0부터 시작합니다.
                size: teamsPerPage
              }
            });

            // 서버 응답 로그 출력
            console.log(response.data.data);

            // 데이터 검증 및 상태 업데이트
            if (response.data && response.data.data) {
              setTeams(Array.isArray(response.data.data.content) ? response.data.data.content : []);
              setTotalPages(response.data.data.page.totalPages || 1);
            } else {
              setTeams([]);
              setTotalPages(1);
            }
          } catch (error) {
            setError(error.message); // 오류 상태 업데이트
          } finally {
            setLoading(false); // 로딩 완료
          }
        };

        fetchTeams(); // 데이터 가져오기 호출
      }, [currentPage]); // currentPage가 변경될 때마다 실행


  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar />
      </div>
        <div>

        </div>

    </div>
  );
}

export default TeamDetail;
