import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css'
import { useParams } from 'react-router-dom';
import axiosInstance from '../../contexts/axiosInstance';


function TeamDetail() {
    const { id } = useParams(); // URL에서 id를 추출
    const [team, setTeam] = useState(); // 퀴즈 데이터를 저장할 상태
    const [loading, setLoading] = useState(true); // 로딩 상태
    const [error, setError] = useState(null); // 오류 상태

    useEffect(() => {
        // 데이터 가져오기
        const fetchTeam = async () => {
          try {
            const response = await axiosInstance.get(`/api/v1/teams/${id}`);
            setTeam(response.data.data);
            console.log(response.data.data);
          } catch (error) {
            setError(error.message); // 오류 상태 업데이트
          } finally {
            setLoading(false); // 로딩 완료
          }
        };

        fetchTeam(); // 데이터 가져오기 호출
      }, [id]); // id가 변경될 때마다 실행

  // 로딩 중 또는 오류 발생 시 처리
  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>오류: {error}</div>;
  if (!team) return <div>팀 정보를 찾을 수 없습니다.</div>; // 팀 데이터가 없을 때 처리

  return (
    <div className="team-page">
      <div className="header-container">
        <Header />  
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar />
      </div>
      <div className='team-detail-content'>
        <div className="team-detail">
            <h2>{team.title}</h2>
        </div>
    </div>
    </div>
  );
}

export default TeamDetail;
