import React, { useState, useEffect } from 'react';
import axiosInstance from '../../contexts/axiosInstance';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import TeamNavbar from '../../components/TeamNavbar';
import '../../css/Team.css';
import { Link } from 'react-router-dom';

function TeamShortProject() {
  const [teams, setTeams] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const teamsPerPage = 12;
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState(""); // 검색어 상태
  const [sortOption, setSortOption] = useState("최신순"); // 정렬 옵션 상태

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await axiosInstance.get('/api/v1/teams/search', {
          params: {
            teamCategory: "COMPETITION",
            page: currentPage,
            size: teamsPerPage,
            searchTerm: searchTerm, // 검색어 추가
            sort: sortOption // 정렬 옵션 추가
          }
        });

        console.log(response.data.data);

        if (response.data && response.data.data) {
          setTeams(Array.isArray(response.data.data.content) ? response.data.data.content : []);
          setTotalPages(response.data.data.page.totalPages || 1);
        } else {
          setTeams([]);
          setTotalPages(1);
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, [currentPage, searchTerm, sortOption]); // currentPage, searchTerm, sortOption이 변경될 때마다 실행

  return (
    <div className="team-page">
      <div className="header-container">
        <Header />
      </div>
      <div className="navbar-container">
        <Navbar />
      </div>
      <div className='teamNavbar-container'>
        <TeamNavbar 
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          sortOption={sortOption}
          setSortOption={setSortOption}
        />
      </div>

      <div className="team-content">
        <div className='team-container'>
          <h2>공모전</h2>        
          <div className="team-list">
            {teams.map(team => (
              <div className="team-card" key={team.id}>
                <Link to={`/team/${team.id}`} className="team-link">
                  <span>{team.teamCategory}</span>
                  <h3>{team.title}</h3>
                  <p>{team.description}</p>
                  <p>팀원 수: {team.memberNumber}명</p>
                </Link>
              </div>
            ))}
          </div>
          {!loading && !error && teams.length === 0 && (
            <p className="no-teams-message">팀 없습니다.</p>
          )}
        </div>

        <div className="pagination">
          {/* 여기에 페이지네이션 추가 가능 */}
        </div>
      </div>
    </div>
  );
}

export default TeamShortProject;
