import React, { useState, useEffect } from 'react';
import '../../css/ResumeCreate.css';
import Navbar from '../../components/Navbar';
import Header from '../../components/Header';
import UserMenubar from '../../components/UserMenubar';
import axiosInstance from '../../contexts/axiosInstance';

function MyResume() {
    const [selectedJobs, setSelectedJobs] = useState([]);
    const [careers, setCareers] = useState([]);

    const jobOptions = [
        '웹 백엔드',
        '웹 프론트엔드',
        'iOS',
        '안드로이드',
        '하이브리드 앱',
        '게임 개발',
        '기타'
    ];

    // Fetch resume data
    const fetchResume = async () => {
        try {
            const response = await axiosInstance.get('/api/v1/resumes/my');
            const { selectedJobs, careerResponses } = response.data.data;
            console.log(response.data);
            setSelectedJobs(selectedJobs || []);
            setCareers(careerResponses || []);
        } catch (error) {
            console.error('Error fetching resume:', error);
        }
    };

    useEffect(() => {
        fetchResume(); // Fetch resume data on mount
    }, []);

    return (
        <div className="resume-create-page">
            <div className="header-container">
                <Header />
                <Navbar />
            </div>
            <div className="user-delete-content">
                <div className="user-menubar-container">
                    <UserMenubar />
                </div>
                <div className="resume-content">
                    <h3 className="resume-job-title">직군 선택</h3>
                    <div className="job-selection-container">
                        {jobOptions.map((job) => (
                            <label key={job} className="job-option">
                                <input
                                    type="checkbox"
                                    checked={selectedJobs.includes(job)}
                                    readOnly
                                />
                                {job}
                            </label>
                        ))}
                    </div>

                    <hr />

                    <h3 className='resume-career-title'>경력</h3>

                    {careers.map((career, index) => (
                        <div key={index} className="career-entry">
                            <input
                                type="text"
                                placeholder="회사명"
                                value={career.company}
                                readOnly
                                className="career-input"
                            />
                            <input
                                type="text"
                                placeholder="직책"
                                value={career.position}
                                readOnly
                                className="career-input"
                            />
                            <input
                                type="text"
                                placeholder="부서명"
                                value={career.department}
                                readOnly
                                className="career-input"
                            />
                            <input
                                type="text"
                                placeholder="직무"
                                value={career.job}
                                readOnly
                                className="career-input"
                            />
                            <input
                                type="date"
                                placeholder="시작 날짜"
                                value={career.startDate}
                                readOnly
                                className="career-input"
                            />
                            <input
                                type="date"
                                placeholder="종료 날짜"
                                value={career.endDate}
                                readOnly
                                className="career-input"
                            />
                            <textarea
                                placeholder="내용"
                                value={career.description}
                                readOnly
                                className="career-description"
                            />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MyResume;
